import React from "react";
import './services.css'

import {Container, Row, Col} from "reactstrap";

const serviceData = [
    {
        icon: 'ri-code-line',
        title: 'Web-дизайн',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugiat labore maiores molestia'
    },
    {
        icon: 'ri-code-s-slash-line',
        title: 'Web-разработка',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugiat labore maiores molestia'
    },
    {
        icon: 'ri-search-line',
        title: 'SEO',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Fugiat labore maiores molestia'
    }
]

const Services = () => {
    return <section id='services'>
        <Container>
            <Row>
                <Col lg='12' className='services__top mb-5'>
                    <h6>Мои услуги</h6>
                    <h2>Какие услуги я оказываю</h2>
                </Col>
                <Col lg='4' md='6' sm='6'>
                    {
                        serviceData.map((service, index) => {
                            return <Service icon={service.icon} title={service.title} desc={service.desc} key={index}/>
                        })
                    }
                </Col>
            </Row>
        </Container>
    </section>
}

const Service = (icon, title, desc) => {
    return <div className="single__service">
                        <span className="service__icon">
                            <i className={icon}></i>
                        </span>

        <h2>{title}</h2>
        <p>{desc}</p>
    </div>
}

export default Services
