import React, {useEffect, useRef} from "react";
import './header.css'
import {Container} from "reactstrap";

const navLinks = [
    {display: 'Home', url: '#home'},
    {display: 'About', url: '#about'},
    {display: 'Services', url: '#services'},
    {display: 'Portfolio', url: '#portfolio'},
    {display: 'Contact', url: '#contact'},
]

const Header = () => {

    const headerRef = useRef(null)

    useEffect(scroll,() => {
        if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80){
            headerRef.current.add('header__shrink')
        }
    }, [])

    return <header className='header'>
        <Container>
            <div className="navigation d-flex align-items-center justify-content-between">
                <div className="logo"><h5>logo</h5></div>
                <div className="nav__menu">
                    <ul className="nav__list">
                        {navLinks.map((link, index) => {
                            return <li className="nav__item" key={index}>
                                <a href={link.url}>{link.display}</a>
                            </li>
                        })}
                    </ul>
                </div>
                <div className="nav__right d-flex align-items-center gap-4">
                    <button className="btn">Let's talk</button>
                    <span className="mobile__menu"><i className="ri-menu-line"></i></span>
                </div>
            </div>
        </Container>

    </header>
}

export default Header
